import React, {useState} from 'react';
import styled from "styled-components";

type imageProp = {
    image?: any
    x?: number
    y?: number
}

const TrackerWrapper = styled.section`
    height: 350px;
    margin: 0;
    left: 0;
    padding: 0;
    position: relative;
    top: 0;
    width: calc(100vw - 50px);
`;

const ImageWrapper = styled.img`
    position: absolute;
    height: 50px;
    width: 50px;
`;

const RegularTracker: React.FC<imageProp> = (props) => {
    const [state, updateState] = useState({x: 0, y: 0});

    let handleMouseMove = (e: any) => {
        updateState({
            x: e.clientX,
            y: e.clientY
        })
    };

    return <TrackerWrapper onMouseMove={handleMouseMove}>
        <ImageWrapper src={props.image} style={{left: state.x, top: state.y}}/>
    </TrackerWrapper>
};

RegularTracker.defaultProps = {
    image: "/src/images/cat.png"
};

export default RegularTracker;
