import React from 'react';
import styled from "styled-components";

type imageProp = {
    tracker: trackerType
    image?: any
}

export type trackerType = {
    x?: number
    y?: number
}

const ImageWrapper = styled.img`
    display: block;
    position: absolute;
    height: 50px;
    width: 50px;
`;

const Image: React.FC<imageProp> = (props) => {
    const {tracker} = props;
    return <><ImageWrapper src={props.image} style={{left: tracker.x}}/></>
};

Image.defaultProps = {
    image: "/src/images/cat.png"
};

export default Image;
