import React from 'react';

type ingredientProps = {
    type: string
}

const Ingredients: React.FC<ingredientProps> = (props) => {
    let ingredients = {};

    switch (props.type) {
        case('ham'):
            ingredients = <div>Ham</div>;
            break;
        case('cheees'):
            ingredients = <div>Cheese</div>;
            break;
        case('pepperoni'):
            ingredients = <div>Pepperoni</div>;
            break;
        case('mushroom'):
            ingredients = <div>Mushroom</div>;
            break;
        default:
            ingredients = {};
    }
    return <>
        {ingredients}
    </>
};

export default Ingredients;
