import React from 'react';
import styled, {ThemeProvider} from 'styled-components';

const MainButton = styled.button`
  background-color: ${props =>
    (props.primary && props.theme.primary)
    || (props.danger && props.theme.danger)
    || ((props.inverted || props.link) && '#fff')
    || (props.disabled && props.theme.brandGrey)
    };
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }

    &--primary {
        background-color: #d0245a;
    }

    &--transparent {
        background-color: transparent;
        border: 2px solid #eaeaea;
    }

    &--cta {
        background-color: #237c7e;
    }
`;

type ButtonProps = {
    class: string,
    children: string,
    type?: string,
    primary: string
}

const Button: React.FC<ButtonProps> = (props) => {
    return <div>
        <MainButton className={props.class}>{props.children}</MainButton>
    </div>

};

export default Button;
