import React from 'react';
import styled from 'styled-components';

type contentProps = {
    header?: string,
    content?: string,
}

const Section = styled.section`
    height: 400px;
    width: 100%;
`;

const Left = styled.section`
    float: left;
    height: 100%;
    padding: 20px;
    width: calc(30% - 40px);
`;

const Right = styled.section`
    float: right;
    height: 100%;
    padding: 20px;
    width: calc(70% - 40px);
`;

const Content:React.FC<contentProps> = (props) => {
    return <Section>
        <Left>
        <h2>{props.header}</h2>
            <p>{props.content}</p>
        </Left>
        <Right>
        {props.children}
        </Right>
    </Section>
};

Content.defaultProps = {
    header: 'This is the header of the content',
    content: ''
};

export default Content;
