import React from 'react';

interface countryProps {
    id?: number
    name?: string
    currency?: string
}

export type countryPropsArray = {
    countries: Array<countryProps>;
}

const specialContext = React.createContext({} as countryPropsArray[]);
export const CountryContextProvider = specialContext.Provider;
export const CountryContextConsumer = specialContext.Consumer;
