import React from 'react';

type ControlProps = {
    label?: string;
    added: any;
    remove: any;
};

const control: React.FC<ControlProps> = (props) => {

    return <>
        <span>{props.label}</span>
        <button onClick={props.added}>+</button>
        <button onClick={props.remove}>-</button>
    </>
};

export default control;
