import React from 'react';
import styled from 'styled-components';

const MainButton = styled.button`
  background-color: ${props => props.pink ? "#d0245a" : "#FFF"};
  border-radius: 3px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 20px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

type ButtonProps = {
    class: string,
    children: string,
    type: string
}

const Button: React.FC<ButtonProps> = (props) => {
    return <MainButton className={props.class}>{props.children}</MainButton>
};

export default Button;
