import React, {useState} from 'react';
import styled from "styled-components";

type imageProp = {
    x?: number
    y?: number
}

const DisplayWrapper = styled.section`
    bottom: 0;
    height: inherit%;
    margin: 0;
    left: 0;
    right: 0;
    padding: 0;
    position: absolute;
    width: calc(100vw - 50px);
`;

const DisplayTracker: React.FC<imageProp> = () => {
    const [state, updateState] = useState({x: 0, y: 0});

    let handleMouseMove = (e: any) => {
        updateState({
            x: e.clientX,
            y: e.clientY
        })
    };

    return <DisplayWrapper onMouseMove={handleMouseMove}>
      <h2>{state.x} | {state.y}</h2>
    </DisplayWrapper>
};

export default DisplayTracker;
