import React from 'react';
import styled from 'styled-components';

type figureProps = {
    imgSource?: string
    caption?: string
    type: string
}

const Fig = styled.figure`
    border-radius: 3px;
    float:left;
    margin: 10px;
    width: 250px;
    img {
        border-radius: 3px;
        width: 100%;
    },
    figcaption {
        font-size: 12px;
        text-align:center;
    }
`;

const SecFig = styled(Fig)`
    border: 3px solid #82BFE0;
`;

const ThirdFig = styled(Fig)`
    border: 3px solid #d0245a;
`;

const ForthFig = styled(Fig)`
    border: 3px solid #237c7e;
`;

const Figure: React.FC<figureProps> = (props) => {
    let showFigure = {};

    switch (props.type) {
        case('primary'): {
            showFigure = <Fig>{props.caption} | primary</Fig>;
        }
            break;
        case('secondary'): {
            showFigure = <SecFig>{props.caption} | secondary</SecFig>;
        }
            break;
        case('third'): {
            showFigure = <ThirdFig>{props.caption} | third</ThirdFig>;
        }
            break;
        case('forth'): {
            showFigure = <ForthFig>{props.caption} | forth</ForthFig>;
        }
    }

    return <>{showFigure}</>
};

Figure.defaultProps = {
    imgSource: '/src/images/image.jpg',
    caption: 'please insert caption or remove it by adding blocked'
};


export default Figure;
