import React, {useState} from 'react';

const Increment: React.FC = () => {
    const [count, setCount] = useState(0);

    const plus = () => {
       setCount(lll => lll + 1);
    };

    const minus = () => {
        setCount(lll => lll - 1);
    };

    return <React.Fragment>
        <button onClick={plus}>+</button>
        <button onClick={minus}>-</button>
        {count}
    </React.Fragment>
};

export default Increment;
