import React, {useState} from 'react'

type stateProps = {
    children: any
    x: number
    y: number
}

let state = {
    x: 0,
    y: 0
};

const Mouse: React.FC<stateProps> = (props) => {
    const handleMouseMove = (e: any) => {
        e.setState({
            x: e.clientX,
            y: e.clientY
        });
    };
    return <div style={{height: '100vh'}} onMouseMove={handleMouseMove}>
        {props.children(state)}
    </div>
};

export default Mouse;
