import React from 'react';
import styled, {ThemeProvider} from 'styled-components';

type ButtonProps = {
    class: string,
    children: string,
    type?: string,
    primary: string
}

const theme = {
    primary: '#d0245a',
    transparent: 'transparent',
    cta: '#237c7e'
};

const MainButton = styled.button`
  background-color: ${props =>
    (props.theme.primary && props.theme.primary)
    || (props.danger && props.theme.danger)
    || ((props.inverted || props.link) && '#fff')
    || (props.disabled && props.theme.brandGrey)
    };
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }

    &--primary {
        background-color: #d0245a;
    }

    &--transparent {
        background-color: transparent;
        border: 2px solid #eaeaea;
    }

    &--cta {
        background-color: #237c7e;
    }
`;

const Button: React.FC<ButtonProps> = (props) => {
    return <ThemeProvider theme={theme}>
        <MainButton className={props.class}>{props.children}</MainButton>
    </ThemeProvider>

};

export default Button;
