import React, {useState} from 'react';

type incrementType = () => void;
type decrementType = () => void;

export type counterProps = {
    count: number;
    incrementCount?: incrementType;
    decrementCount?: decrementType;
    wrappedComponent: any;
}

const Counter: React.FC<counterProps> = (WrappedComponent) => {
    const [count, setCount] = useState(0);

    const incrementCount = () => {
        return setCount(c => c + 1);
    };

    const decrementCount = () => {
        return setCount(c => c - 1);
    };

    return (wrappedComponent: any) => (
        <WrappedComponent count={count}
                           incrementCount={incrementCount}
                           decrementCount={decrementCount}>{wrappedComponent}</WrappedComponent>
    );
};

export default Counter;
