import React from 'react';
import styled from "styled-components";
import {trackerType} from '../Tracker/Tracker';

type imageProp = {
    tracker: trackerType
    image?: any
}

const ImageWrapper = styled.img`
    position: absolute;
    height: 50px;
    width: 50px;
`;

const Image: React.FC<imageProp> = (props) => {
    const {tracker} = props;
    return <><ImageWrapper src={props.image} style={{left: calc(tracker.x - 50), top: tracker.y}}/></>
};

Image.defaultProps = {
    image: "/src/images/cat.png"
};

export default Image;
