import React, {useState} from 'react';
import styled from 'styled-components';

type ButtonProps = {
    addClass?: string
    content?: string
    myClass?: string
    name?: string
    children?: string
    value?: string
    onclick?: {}
    kind?: string[]
}

const MainButton = styled.button`
  background-color: ${props => props.theme.brandBlue};
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &.click {
    background-color: green;
    }

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

const Span = styled.span`
    display: none;

    &.view {
    display: block;
    }
`;

const Button: React.FC<ButtonProps> = (props) => {
    const [addClass, setClass] = useState(props.myClass);

    const buttonClick = () => {
        setClass('click');
    };

    return <>
        <MainButton className={addClass} name={props.name} value={props.value} onClick={buttonClick}>{props.children}</MainButton>
        <span>{props.content}</span>
    </>
};

Button.defaultProps = {
    myClass: 'button',
    name: 'button',
    value: 'Click',
    children: 'Click button'
};

export default Button;
