import React, {useState} from 'react';
import styled from 'styled-components';

type InputFieldType = {
    name: string
    type: string
    input?: string
    placeholder?: string
    value?: string
}

const Input = styled.input`
    border: 1px solid #eee;
    border-radius: 3px;
    font-size: 16px;
    margin: 10px 0;
    padding: 10px 5px;
    outline: none;
    width: 250px;
`;


const InputField: React.FC<InputFieldType> = (props) => {
    const [input, setInput] = useState('');

    const handleChange = (e: any) => {
        setInput(e.target.value);
        if (e.target.value.length == 20) {
            alert("almost reaching limit");
        }
    };

    const handleBlur = () => {

    };

    return <React.Fragment>
        <Input name={props.name} value={input} type={props.type} placeholder={props.placeholder} onChange={e => handleChange(e)} onBlur={() => handleBlur()}/>
        <span>{input}</span>
    </React.Fragment>
};

InputField.defaultProps = {
    name: 'input name',
    type: 'name',
    placeholder: 'Name'
};

export default InputField;
