import React, {useState} from 'react'

type stateProps = {
    x: number
    y: number
}

let state = {
    x: 0,
    y: 0
};

const Mouse: React = (props) => {
    const handleMouseMove = (e: any) => {
        this.setState({
            x: e.clientX,
            y: e.clientY
        });
    };
    render()
    {
        return (<div style={{height: '100vh'}} onMouseMove={handleMouseMove}>
            {props.render(state)}
        </div>
        );
    }
};

export default Mouse;
