import React from 'react'

type stateProps = {
    x: number
    y: number
}

let state = {
    x: 0,
    y: 0
};

const handleMouseMove = (e) => {
    state.setState({
        x: e.clientX,
        y: e.clientY
    });
};

const Mouse: React.FC = () => {
    return <div style={{ height: '100vh' }} onMouseMove={handleMouseMove}>
        <p>The current mouse position is ({state.x}, {state.y})</p>
    </div>
};

export default Mouse;
