import React from 'react';

export type countryProps = {
    id: number,
    name: string,
    currency: string
    weather: string
}

export type countryPropsList = countryProps[];

const specialContext = React.createContext<countryPropsList | null>(null);

export const UserContextProvider = specialContext.Provider;

export const UserContextConsumer = specialContext.Consumer;
