import React from 'react';

const style = `
    opacity: 0.8;

    figcaption {
        display: none;
    }
`;

const Disabled = (WrappedComponent: any) => {
    return (props:any) => (
        <WrappedComponent style={style} {...props}>{props}</WrappedComponent>
    );
};

export default Disabled;
