import React from 'react';
import styled from 'styled-components';

type ButtonProps = {
    class?: string,
    name?: string,
    children?: string,
    value?: string,
    type?: string,
    onclick?: {}
}

const MainButton = styled.button<{primary: boolean, cta: boolean}>`
   ${props => props.primary && `
    background-color: ${props.theme.primary};
  `}
  ${props => props.cta && `
    background-color: ${props.theme.cta};
  `}
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

const Button: React.FC<ButtonProps> = (props) => {
    return <MainButton className={props.class} name={props.name} value={props.value} type={props.type}>{props.children}</MainButton>
};

Button.defaultProps ={
    name: 'button',
    value: 'Click',
    children: 'Click button'
};

export default Button;
