import React from 'react';
import styled from 'styled-components';

type figureProps = {
    imgSource?: string
    caption?: string
    type: string
}

const Fig = styled.figure`
    float:left;
    margin: 10px;
    width: 250px;
    img {
        border-radius: 3px;
        width: 100%;
    },
    figcaption {
        font-size: 12px;
        text-align:center;
    }
`;

const Figure: React.FC<figureProps> = (props) => {
    let showFigure = {};

    switch (props.type) {
        case('primary'): {
            showFigure = <Fig>{props.caption} | primary</Fig>;
        }
            break;
        case('secondary'): {
            showFigure = <Fig>{props.caption} | secondary</Fig>;
        }
            break;
        case('third'): {
            showFigure = <Fig>{props.caption} | third</Fig>;
        }
            break;
        case('forth'): {
            showFigure = <Fig>{props.caption} | forth</Fig>;
        }
    }

    return <>{showFigure}</>
};

Figure.defaultProps = {
    imgSource: '/src/images/image.jpg',
    caption: 'please insert caption or remove it by adding blocked'
};


export default Figure;
