import React from 'react';
import styled, {ThemeProvider} from 'styled-components';

type ButtonProps = {
    class?: string,
    children: string,
    type?: string,
    primary: string,

}

const theme = {
    primary: '#d0245a',
    transparent: 'transparent',
    cta: '#237c7e'
};

const MainButton = styled.button`
  background-color: ${props =>
    (props.theme.primary && props.primary)
    || (props.theme.transparent && props.transparent)
    || ((props.theme.cta || props.cta) && '#fff')
    };
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

const Button: React.FC<ButtonProps> = (props) => {
    return <ThemeProvider theme={theme}>
        <MainButton primary className="btn">{props.children}</MainButton>
        <MainButton className="btn">{props.children}</MainButton>
    </ThemeProvider>

};

export default Button;
