import React from 'react';
import styled from 'styled-components';

type ListProps<T> = {
    children: T
    class: string
};

const ListContent = styled.li`
    margin: 10px;
`;

const List = <T extends []>({children}: ListProps<T>) => {
    return <ul className={className}>
        {children.map((content) => (
            <ListContent>{content}</ListContent>
        ))}
    </ul>
};

export default List;


const addClass = (WrappedComponent: any, className: string) => {
    return (children:any) => (
        <div className={className}>
            <WrappedComponent {...children}>{children}</WrappedComponent>
        </div>
    );
};

export default addClass;
