import React from 'react';
import styled from 'styled-components';

type ButtonProps = {
    class?: string,
    color: string,
    children: string,
    type?: string,

}

// const theme = {
//     primary: '#d0245a',
//     transparent: 'transparent',
//     cta: '#237c7e'
// };

const MainButton = styled.button`
  background-color: #fff;
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

const Button: React.FC<ButtonProps> = (props) => {
    return <MainButton className="btn" >{props.children}</MainButton>
};

export default Button;
