import React, {useState} from 'react';

export interface trackerType {
    x?: number
    y?: number
}

const Tracker: React.FC<trackerType> = (props) => {
    const [state, updateState] = useState({x: 0, y: 0});

    const handleMouseMove = (e: any) => {
        updateState({
            x: e.event.clientX,
            y: e.event.clientY
        })
    };

    return <div onMouseMove={handleMouseMove}>
        <div>{state.x}</div>
    </div>
};

export default Tracker;
