import React from 'react';
import styled from 'styled-components';

const MainButton = styled.button`
  background-color: #FFF;
  border-radius: 3px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 20px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }

    &--primary {
        background-color: #d0245a;
    }

    &--danger {
        background-color: #red;
    }

    &--cta {
        background-color: #237c7e;
    }
`;

const BlueButton = styled(MainButton)`
 background-color: blue;
`;

type ButtonProps = {
    class: string,
    children: string,
    type?: string
}

const Button: React.FC<ButtonProps> = (props) => {
    return <div>
        <MainButton className={props.class}>{props.children}</MainButton>
        <BlueButton className={props.class}>{props.children}</BlueButton>
    </div>
};

export default Button;
