import React, {useState} from 'react';

type incrementType = () => void;
type decrementType = () => void;

type counterProps = {
    count: number
    regIncrementCount: incrementType
    regDecrementCount: decrementType
}

const RegularIncrement: React.FC<counterProps> = () => {
    const [count, setCount] = useState(0);

    const regIncrementCount = () => {
        return setCount(c => c + 1);
    };

    return <React.Fragment>
        <button onClick={regIncrementCount}>+</button>
        <span>The count is {count}</span>
    </React.Fragment>
};

export default RegularIncrement;
