import React from 'react';
import styled from 'styled-components';

type figureProps = {
    imgSource?: string
    caption?: string
    type: string
}

const Fig = styled.figure`
    float:left;
    margin: 10px;
    width: 250px;
    img {
        border-radius: 3px;
        width: 100%;
    },
    figcaption {
        font-size: 12px;
        text-align:center;
    }
`;

const Figure: React.FC<figureProps> = (props) => {
    let showFigure = {};

    switch (props.type) {
        case('primary'): {
            showFigure = (<>
                <Fig>first fig</Fig>
                <figcaption>{props.caption}</figcaption>
                <img src={props.imgSource}/>
            </>);
        }
            break;
        case('secondary'): {
            showFigure = (<>
                <Fig>first fig</Fig>
                <figcaption>{props.caption}</figcaption>
                <img src={props.imgSource}/>
            </>);
        }
            break;
        case('third'): {
            showFigure = (<>
                <Fig>first fig</Fig>
                <figcaption>{props.caption}</figcaption>
                <img src={props.imgSource}/>
            </>);
        }
            break;
        case('forth'): {
            showFigure = <>
                <Fig>first fig</Fig>
                <figcaption>{props.caption}</figcaption>
                <img src={props.imgSource}/>
            </>
        }
    }

    return {showFigure}
};

Figure.defaultProps = {
    imgSource: '/src/images/image.jpg',
    caption: 'please insert caption or remove it by adding blocked'
};


export default Figure;
