import React from 'react'

type stateProps = {
    x: number
    y: number
}

class Mouse extends React.Component<stateProps> {
    constructor(props){
        super(props);
        this.handleMouseMove = this.handleMouseMove.bind(this);
        this.state = { x: 0, y: 0 };
    }

    handleMouseMove(e) {
        this.setState({
            x: e.clientX,
            y: e.clientY
        });
    }

    render() {
        return (
          <div>
              {this.handleMouseMove}
              {this.props(this.state)}
          </div>
        );
    }
}

export default Mouse;
