import React from 'react';
import styled from 'styled-components';

type contentProps = {
    header?: string,
    content?: string,
}

const Section = styled.section`
    height: 350px;
    float: left;
    margin-top: 25px;
    width: 100%;
`;

const Content: React.FC<contentProps> = (props) => {
    return <Section>
        {props.children}
    </Section>
};

Content.defaultProps = {
    header: 'This is the header of the content',
    content: ''
};

export default Content;
