import React from 'react';
import styled, {ThemeProvider} from 'styled-components';
import theme from 'styled-theming';
import Blockquote from '../components/Blockquote/Blockquote';
import Button from '../components/Button/Button';
import Content from '../components/Content/Content';
import ContactForm from '../components/Form/Contact/ContactForm';
import Figure from '../components/Figure/Figure';
import Pagination from '../components/Pagination/Pagination';
import Layout from '../Layout/Layout';

const themeSetup = theme.variants('colorSet', 'themeSet', {
    primary: {backgroundColor: '#d0245a'},
    transparent: {backgroundColor: 'transparent'},
    cta: {backgroundColor: '#237c7e'}
});

type ButtonProps = {
    themeSet: ['primary', 'transparent', 'cta']
}

const ButtonStyled = styled(Button)`
  ${themeSetup}
`;

const Components: React.FC<ButtonProps> = () => {
    return (
        <ThemeProvider theme={{colorSet: 'backgroundColor'}}>
            <Layout page="Components">
                <Content header="this is the button component">
                    <ButtonStyled>Click</ButtonStyled>
                    <ButtonStyled>Click 2</ButtonStyled>
                    <ButtonStyled>Click 3</ButtonStyled>
                </Content>
                <Content header="this is the figure component">
                    <Figure/>
                    <Figure/>
                    <Figure/>
                </Content>
                <Content header="this is the contact form component">
                    <ContactForm title="contact form" class="contact-form" action="nigelmaduro14@gmail.com"/>
                </Content>
                <Content header="this is the blockquote component">
                    <Blockquote></Blockquote>
                    <Blockquote></Blockquote>
                    <Blockquote></Blockquote>
                </Content>
                <Content header="this is the pagination component">
                    <Pagination pages={[1, 2, 3, 4, 5, 6, 8]}>pagination</Pagination>
                </Content>
            </Layout>
        </ThemeProvider>
    )
};

export default Components;
