import React, {useState, useEffect} from 'react';
import styled from 'styled-components';

type InputFieldType = {
    name: string
    type: string
    input?: string
    placeholder?: string
    value?: string
}

const Input = styled.input`
    border: 1px solid #eee;
    border-radius: 3px;
    font-size: 16px;
    margin: 10px 10px 10px 0;
    padding: 10px 5px;
    outline: none;
    width: 250px;
`;

const InputField: React.FC<InputFieldType> = (props) => {
    const [input, setInput] = useState('');

    // values for setting up warning length and input length
    const lengthWarning: number = 15;
    const lengthMax: number = 20;
    let warningText;

    const handleChange = (e: any) => {
        setInput(e.target.value);
        if (e.target.value.length == lengthWarning) {
            return warningText = <span>max text length is almost reached</span>
        } else {
            console.log('nada');
        }
    };

    const handleBlur = () => {

    };

    useEffect(() => {
        console.log('input changed!');
    }, [input]);

    // const onKeyUp = (target, e) => {
    //     if(e.keyCode === 13){
    //         swith(target) {
    //             case {props.name}:
    //             {this.props.name}
    //         }
    //     }
    // };

    return <React.Fragment>
        <Input maxLength={lengthMax} name={props.name} value={input}
               type={props.type} placeholder={props.placeholder}
               onChange={e => handleChange(e)} onBlur={() => handleBlur()}
        />
        <span>{input}</span>
        {warningText}
    </React.Fragment>
};

InputField.defaultProps = {
    name: 'input name',
    type: 'name',
    placeholder: 'Name'
};

export default InputField;
