import React from 'react';
import addClass from '../hoc/AddClass/AddClass';

type paginationProps = {
    pages: number[]
    class?: string
};

const Pagination: React.FC = (props:paginationProps) => {
    return <React.Fragment>
        <button>Previous</button>
        {props.pages.map((page, i) => (
            <button ref={(btnFocus) => {this.ButtonElement = btnFocus}} className={props.class} key={i}>{page}</button>
        ))};
        <button>Next</button>
    </React.Fragment>
};

export default addClass(Pagination, 'pagination');
