import React from 'react';
import styled from 'styled-components';

type ButtonProps = {
    class?: string,
    name?: string,
    children?: string,
    value?: string,
    onclick?: {}
}

const MainButton = styled.button`
  backgroundColor: ${themeSetup}; 
  border-radius: 2px;
  border: none;
  color: #FFF;
  font-size: 16px;
  margin: 0 10px;
  padding: 10px 40px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

const Button: React.FC<ButtonProps> = (props) => {
    return <MainButton className={props.class} name={props.name} value={props.value}>{props.children}</MainButton>

};

Button.defaultProps ={
    name: 'button',
    value: 'Click',
    children: 'Click button'
};

export default Button;
