import React, {useState} from 'react';

type newCounterType = ({}) => {};
type incrementType = ({}) => {};
type decrementType = ({}) => {};

export type counterProps = {
    count: number;
    incrementCount: incrementType;
    decrementCount: decrementType;
}

const Counter = (OriginalComponent: any) => {
    const [count, setCount] = useState(0);

    const incrementCount = () => {
        return setCount(c => c + 1);
    };

    const decrementCount = () => {
        return setCount(c => c - 1);
    };

    return (newCounter: newCounterType) => (
        <OriginalComponent count={count} incrementCount={incrementCount} decrementCount={decrementCount} {...newCounter}>{newCounter}</OriginalComponent>
    );
};

export default Counter;
