import React from 'react';
import styled from 'styled-components';

type contentProps = {
    header?: string,
    content?: string,
}

const Section = styled.section`
    height: 350px;
    float: left;
    margin-top: 25px;
    width: 100%;
`;

const Left = styled.section`
    float: left;
    height: 100%;
    padding: 0 20px;
    width: calc(25% - 40px);

    h3 {
    margin: 0;
    }
`;

const Right = styled.section`
    float: right;
    height: 100%;
    padding: 0 20px;
    width: calc(75% - 40px);
`;

const Content:React.FC<contentProps> = (props) => {
    return <Section>
        <Left>
        <h3>{props.header}</h3>
            <p>{props.content}</p>
        </Left>
        <Right>
        {props.children}
        </Right>
    </Section>
};

Content.defaultProps = {
    header: 'This is the header of the content',
    content: ''
};

export default Content;
