import React from 'react';
import styled from 'styled-components';
import InputField from '../../../components/InputField/InputField';
import Button from '../../../components/Button/Button';

type contactFormProps = {
    action: string,
    class: string,
    title: string
    mail: string
}

const Textarea = styled.textarea`
    height: 100px;
    border: 1px solid #eee;
    border-radius: 3px;
    font-size: 16px;
    margin: 10px 0;
    padding: 10px 5px;
    outline: none;
    resize: none;
    width: 250px;
`;

const ContactForm: React.FC<contactFormProps> = (props) => {
    return <form className={props.class} action={props.action}>
        <fieldset>
            <legend>{props.title}</legend>
            <div>
                <label>Name:</label>
                <InputField name="name" type="name"></InputField>
            </div>
            <div>
                <label>Email:</label>
                <InputField name="email" type="email" placeholder="Email"></InputField>
            </div>
            <div>
                <label>Message:</label>
                <Textarea placeholder="Message"></Textarea>
            </div>
            <Button>Send</Button>
            {sendMail => sendMail.input ? <p>{sendMail.input}</p> : <p>Send mail</p>
            }
        </fieldset>
    </form>
};

ContactForm.defaultProps = {
    action: '/components'
};

export default ContactForm;
