import React from 'react';
import addClass from '../hoc/AddClass/AddClass';

type paginationProps = {
    pages: number[]
    class?: string
};

const Pagination = (props:paginationProps) => {
    const buttonFocusRef = React.useRef();
    buttonFocusRef.current.focus();

    return <React.Fragment>
        <button onClick={clicked}>Previous</button>
        {props.pages.map((page, i) => (
            <button ref={this.buttonFocusRef} className={props.class} key={i}>{page}</button>
        ))}
        <button onClick={clicked}>Next</button>
    </React.Fragment>
};

export default addClass(Pagination, 'pagination');
