import React from 'react';
import styled from 'styled-components';

// type ListProps<T> = {
//     children: T
//     class: string
// };

const ListContent = styled.li`
    margin: 10px;
`;

const List = (className: string) => {
    return (children: any) => (
        <ul className={className}>
                <ListContent>{children}</ListContent>
        </ul>
    );
};

export default List;


const addClass = (WrappedComponent: any, className: string) => {
    return (children: any) => (
        <div className={className}>
            <WrappedComponent {...children}>{children}</WrappedComponent>
        </div>
    );
};

export default addClass;
