import React from 'react';
import styled from 'styled-components';

type blockQuoteProps = {
    content?: string
}

const BlockquoteContent = styled.blockquote`
    float: left;
    font-size: 42px;
    font-style: italic;
    line-height: 42px;
    margin: 10px auto;
    text-align: center;
    width: calc(100% - 40px);

    &:after {
        content: '"';
    }
    &:before {
        content: '"';
    }
`;

const Blockquote: React.FC<blockQuoteProps> = (props) => {
    {if (props.children){
       curContent =  props.children;
    } else {
        defContent = props.content;
    }}
    return <BlockquoteContent>{props.children}</BlockquoteContent>
};

Blockquote.defaultProps = {
    content: 'Blockquote content'
};

export default Blockquote;
