import React, {useState} from 'react'

type stateProps = {
    x: number
    y: number
}

const Mouse = (props:stateProps) => {
    let state = {
        x: 0,
        y: 0
    };

    const handleMouseMove = (e: any) => {
        let newState = state;
        newState.setState({
            x: e.clientX,
            y: e.clientY
        });
    };
    return <div style={{height: '100vh'}} onMouseMove={handleMouseMove}>
        {props}
    </div>
};

export default Mouse;
