const HanChanges: React.FC = () => {
    const [input, setInput] = useState('');

    const handleChange = (e: any) => {
        setInput(e.target.value);
        if (e.target.value.length == 20) {
            console.log("almost reaching limit");
        }
    };

    const handleBlur = () => {

    };

    return <>
        <input onChange={e => handleChange(e)} onBlur={() => handleBlur()}>{input}</input>
    </>
};

export default HanChanges;
