import React from 'react';
import addClass from '../hoc/AddClass/AddClass';

type paginationProps = {
    id: number
    pages: number[]
    class?: string
};

const Pagination = (props:paginationProps) => {
    let totalPages: any[] = props.pages;
    return <React.Fragment>
        <button>Previous</button>
        {totalPages.map((page) => (
            <button className={props.class} key={page.id}>{page}</button>
        ))}
        <button>Next</button>
    </React.Fragment>
};

export default addClass(Pagination, 'pagination');
