import React from 'react';
import styled from 'styled-components';

type LogoProps = {
    logo?: string,
    alt?: string;
    path?: string
}

const Figure = styled.img`
    background: 50% 50% no-repeat;
    background-size: cover;
`;

const Logo: React.FC<LogoProps> = (props) => {
    return <a href={props.path}> <Figure src={props.logo} alt={props.alt}>
    </Figure></a>
};

Logo.defaultProps ={
  logo: './scr/images/frmwrk_logo.svg',
  alt: 'logo',
  path: '/'
};

export default Logo;
