import React from 'react';
import styled, {ThemeProvider} from 'styled-components';
import theme from 'styled-theming';

const buttonFontSize = theme.variants('layout', 'kind', {
    small: {compact: '0.9rem', cozy: '1.2rem'},
    medium: {compact: '1rem', cozy: '1.3rem'},
    large: {compact: '1.1rem', cozy: '1.4rem'},
});

type ButtonProps = {
    class?: string,
    name?: string,
    children?: string,
    value?: string,
    onclick?: {},
    kind?: string[];
}

const MainButton = styled.button`
  font-size: ${buttonFontSize};
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

const Button: React.FC<ButtonProps> = (props) => {
    return <ThemeProvider theme={{layout: 'compact'}}>
        <MainButton className={props.class} name={props.name} value={props.value}>{props.children}</MainButton>
    </ThemeProvider>
};

Button.defaultProps = {
    name: 'button',
    value: 'Click',
    children: 'Click button',
    kind: ['small']
};

export default Button;
