import React from 'react';

interface countryProps {
    id: number
    name: string
    currency: string
}

export type countryPropsList = countryProps[];

const specialContext = React.createContext<countryPropsList | null>(null);

export const CountryContextProvider = specialContext.Provider;
export const CountryContextConsumer = specialContext.Consumer;
