import React from 'react';
import styled, {ThemeProvider} from 'styled-components';

type ButtonProps = {
    class?: string,
    color: string,
    children: string,
    type?: string
}

type theme = {
    primary: boolean,
    cta: boolean,
    transparent: boolean
}

const MainButton = styled.button<theme>`
  background-color: ${props => props.primary && props.theme.primary || props.transparent && props.theme.transparent || props.cta && props.theme.cta};
  border-radius: 2px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 40px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }
`;

const theme = {
    primary: '#d0245a',
    transparent: 'transparent',
    cta: '#237c7e'
};

const Button: React.FC<ButtonProps> = (props) => {
    return <div>
        <ThemeProvider theme={theme}>
            <span>This is the Button component:</span>
            <MainButton className="btn" >{props.children}</MainButton>
        </ThemeProvider>
    </div>
};

export default Button;
