import React from 'react';
import styled from 'styled-components';
import {ThemeProvider} from 'styled-components';

const theme = require('sass-extract-loader?{"plugins": ["sass-extract-js"]}!./vars.scss');

const MainButton = styled.button`
  background-color: ${props =>
    (props.primary && '$primary')
    || (props.transparent && '$transparent')
    || ((props.inverted || props.link) && '#fff')
    || (props.disabled && '$brand-grey')
    };
  border-radius: 3px;
  border: none;
  color: #FFF;
  margin: 0 10px;
  padding: 10px 20px;

    &:hover {
        background-color: #000;
        cursor: pointer;
    }

    &--primary {
        background-color: #d0245a;
    }

    &--transparent {
        background-color: transperant;
        border: 2px solid #eaeaea;
    }

    &--cta {
        background-color: #237c7e;
    }
`;

type ButtonProps = {
    class: string,
    children: string,
    type?: string
}

const Button: React.FC<ButtonProps> = (props) => {
    return <ThemeProvider theme={theme}>
        <div>
            <MainButton className={props.class}>{props.children}</MainButton>
        </div>
    </ThemeProvider>
};

export default Button;
